package casting;


public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=100;
		long l=i;
		float f1=l;
		System.out.println("Before type casting ,int value:" +i);
		System.out.println("After type casting ,int to long value:" +l);
		System.out.println("After type casting ,int to float value:" +f1);

	}

}
